mapper.update:

update tb_crm_product_category
set parent_id = #{parentId,jdbcType=INTEGER},
  NAME = #{name,jdbcType=VARCHAR},
  description = #{description,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:

select id, parent_id, NAME, description
from tb_crm_product_category
where id = #{id,jdbcType=INTEGER}

select t.id, t.parent_id, t.NAME, t.description,count(p.id) as productNum from tb_crm_product_category t 
left join tb_crm_product p on p.category_id=t.id group by t.id


limit #{offset},#{pageSize}
